/* com.innobiz.orange.web.ct.dao.CtBullMastBDao.updateReadCnt */
UPDATE CT_BULL_MAST_B

	READ_CNT = READ_CNT + 1,


	AND BULL_ID = #{bullId, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtBullMastBDao.updateRecmdCnt */
UPDATE CT_BULL_MAST_B

	RECMD_CNT = RECMD_CNT + 1,


	AND BULL_ID = #{bullId, jdbcType=INTEGER}

/* com.innobiz.orange.web.ct.dao.CtBullMastBDao.updateScre */
UPDATE CT_BULL_MAST_B

	SCRE = (SELECT AVG(SCRE) FROM BA_SCRE_HST_L WHERE BULL_ID = CT_BULL_MAST_B.BULL_ID)


	AND BULL_ID = #{bullId, jdbcType=INTEGER}

