mapper.select:
/* com.innobiz.orange.web.ct.dao.CtBullMastCmdDDao.countCtBullMastCmdD */
SELECT COUNT(*) CNT
FROM CT_BULL_MAST_CMD_D CBMCD_T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtBullMastCmdDDao.insertCtBullMastCmdD */ INSERT INTO CT_BULL_MAST_CMD_D
CMT_ID, BULL_ID, CMT, REGR_UID, REG_DT,
#{cmtId, jdbcType=NUMERIC}, #{bullId, jdbcType=NUMERIC}, #{cmt, jdbcType=NVARCHAR}, #{regrUid, jdbcType=VARCHAR}, SYSDATE,NULL, TO_DATE(#{regDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),

mapper.update:
	/* com.innobiz.orange.web.ct.dao.CtBullMastCmdDDao.updateCtBullMastCmdD */
	UPDATE CT_BULL_MAST_CMD_D
	
		 CMT = #{cmt, jdbcType=NVARCHAR},
	
	
	     AND CMT_ID = #{cmtId, jdbcType=NUMERIC}
	     AND BULL_ID = #{bullId, jdbcType=NUMERIC}

mapper.delete:
	/* com.innobiz.orange.web.ct.dao.CtBullMastCmdDDao.deleteCtBullMastCmdD */
	DELETE FROM CT_BULL_MAST_CMD_D
	
	     AND CMT_ID = #{cmtId, jdbcType=NUMERIC}
	     AND BULL_ID = #{bullId, jdbcType=NUMERIC}
