mapper.select:
		/* com.innobiz.orange.web.ct.dao.CtCatBDao.selectCtCatB */
		
		
		SELECT * FROM (
		
		SELECT
		
		
			
			* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
			
			CCB_T.COMP_ID, CCB_T.CAT_ID, CCB_T.CAT_SUBJ_RESC_ID, CCB_T.CAT_PID,
			CCB_T.CAT_LOC_STEP, CCB_T.CHLD_YN, CCB_T.EXTN_OPEN_YN, CCB_T.FLD_TYP_CD,
			CCB_T.REG_DT, CCB_T.MOD_DT, CCB_T.REGR_UID, CCB_T.MODR_UID,
			CCB_T.CAT_ORDR,
	      	 
	      	(
				SELECT COUNT(*) FROM CT_BLON_CAT_D CBCD_T
					INNER JOIN CT_ESTB_B CEB_T ON CBCD_T.CT_ID = CEB_T.CT_ID
						WHERE CBCD_T.CAT_ID = (
													SELECT CAT_ID FROM CT_CAT_B 
														WHERE CAT_ID = #{catId, jdbcType=VARCHAR}
															AND FLD_TYP_CD = #{fldTypCd, jdbcType=VARCHAR}
											  ) AND CEB_T.CT_ACT_STAT = 'A'
			) AS CAT_CMNT_CNT,
			
			(
				SELECT RESC_VA 
				FROM CT_RESC_B
				WHERE RESC_ID = CCB_T.CAT_SUBJ_RESC_ID
				AND LANG_TYP_CD = #{langTyp, jdbcType=VARCHAR}
      		) CAT_NM,
      		
      		(
				SELECT CAT_SUBJ_RESC_ID 
				FROM CT_CAT_B
				WHERE CAT_ID=CCB_T.CAT_PID
	      	) CAT_PID_RES_ID,
      		
      		(
      			SELECT RESC_VA 
      			FROM CT_RESC_B
				WHERE RESC_ID  = (
									SELECT CAT_SUBJ_RESC_ID 
									FROM CT_CAT_B
									WHERE CAT_ID=CCB_T.CAT_PID
								 )
			   AND LANG_TYP_CD = #{langTyp, jdbcType=VARCHAR}
	      )CAT_PID_NM,
      		

		
		FROM CT_CAT_B CCB_T
		
		ORDER BY ${orderBy}
		ORDER BY CCB_T.CAT_ID
		
		) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.ct.dao.CtCatBDao.countCtCatB */
SELECT COUNT(*) CNT
FROM CT_CAT_B CCB_T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtCatBDao.insertCtCatB */ INSERT INTO CT_CAT_B
COMP_ID, CAT_ID, CAT_SUBJ_RESC_ID, CAT_PID, CAT_LOC_STEP, CHLD_YN, EXTN_OPEN_YN, FLD_TYP_CD, REG_DT, MOD_DT, REGR_UID, MODR_UID, CAT_ORDR,
#{compId, jdbcType=CHAR}, #{catId, jdbcType=VARCHAR}, #{catSubjRescId, jdbcType=VARCHAR}, #{catPid, jdbcType=VARCHAR}, #{catLocStep, jdbcType=NUMERIC}, #{chldYn, jdbcType=CHAR}, #{extnOpenYn, jdbcType=CHAR}, #{fldTypCd, jdbcType=VARCHAR}, TO_DATE(#{regDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{regrUid, jdbcType=VARCHAR}, #{modrUid, jdbcType=VARCHAR}, #{catOrdr, jdbcType=NUMERIC},

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtCatBDao.updateCtCatB */ UPDATE CT_CAT_B
CAT_SUBJ_RESC_ID = #{catSubjRescId, jdbcType=VARCHAR}, CAT_PID = #{catPid, jdbcType=VARCHAR}, CAT_LOC_STEP = #{catLocStep, jdbcType=NUMERIC}, CHLD_YN = #{chldYn, jdbcType=CHAR}, EXTN_OPEN_YN = #{extnOpenYn, jdbcType=CHAR}, FLD_TYP_CD = #{fldTypCd, jdbcType=VARCHAR}, MOD_DT = TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), MODR_UID = #{modrUid, jdbcType=VARCHAR}, CAT_ORDR = #{catOrdr, jdbcType=NUMERIC},
AND COMP_ID = #{compId, jdbcType=CHAR} AND CAT_ID = #{catId, jdbcType=VARCHAR}

mapper.delete:
		/* com.innobiz.orange.web.ct.dao.CtCatBDao.deleteCtCatB */
		DELETE FROM CT_CAT_B
		
		    AND COMP_ID = #{compId, jdbcType=CHAR}
   		    AND CAT_ID = #{catId, jdbcType=VARCHAR}
   		    AND FLD_TYP_CD = #{fldTypCd, jdbcType=VARCHAR}
