mapper.select:
/* com.innobiz.orange.web.ct.dao.CtRecMastBDao.countCtRecMastB */ SELECT COUNT(*) CNT FROM CT_REC_MAST_B CRMB_T
INNER JOIN CT_FILE_D CFD_T ON CRMB_T.CT_ID = CFD_T.CT_ID AND CRMB_T.BULL_ID = CFD_T.REF_ID
LEFT JOIN OR_USER_B U ON CRMB_T.REGR_UID = U.USER_UID LEFT JOIN OR_RESC_B R ON U.RESC_ID = R.RESC_ID AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')

