mapper.select:
/* com.innobiz.orange.web.ct.dao.CtScreHstLDao.selectCtScreHstL */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	CSH_T.BULL_ID,
	CSH_T.USER_UID,
	CSH_T.SCRE,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = CSH_T.USER_UID) AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')) USER_NM,
	CSH_T.REG_DT,

FROM CT_SCRE_HST_L CSH_T

	 AND CSH_T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND CSH_T.USER_UID = #{userUid, jdbcType=VARCHAR}
	 ${whereSqllet}

ORDER BY ${orderBy}
ORDER BY CSH_T.REG_DT DESC

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.ct.dao.CtScreHstLDao.countCtScreHstL */
SELECT COUNT(*) CNT
FROM CT_SCRE_HST_L CSH_T

	 AND CSH_T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND CSH_T.USER_UID = #{userUid, jdbcType=VARCHAR}
	 ${whereSqllet}

/* com.innobiz.orange.web.ct.dao.CtScreHstLDao.selectAvgScre */
SELECT AVG(SCRE)
FROM CT_SCRE_HST_L CSH_T

	 AND CSH_T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 ${whereSqllet}

