mapper.select:
/* com.innobiz.orange.web.ct.dao.CtSurvQuesDDao.countCtSurvQuesD */
SELECT COUNT(*) CNT
FROM CT_SURV_QUES_D CSQD_T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtSurvQuesDDao.insertCtSurvQuesD */ INSERT INTO CT_SURV_QUES_D
QUES_ID, COMP_ID, SURV_ID, QUES_SORT_ORDR, QUES_CONT, QUES_IMG_ATT_YN, IMG_SURV_FILE_ID, EXAM_CHOI_ITEM_CD, MUL_CHOI_YN,MANDA_REPLY_YN,
#{quesId, jdbcType=VARCHAR},#{compId, jdbcType=CHAR},#{survId, jdbcType=VARCHAR}, #{quesSortOrdr, jdbcType=NUMERIC},#{quesCont, jdbcType=NVARCHAR},#{quesImgAttYn, jdbcType=CHAR}, #{imgSurvFileId, jdbcType=VARCHAR},#{examChoiItemCd, jdbcType=VARCHAR},#{mulChoiYn, jdbcType=CHAR}, #{mandaReplyYn, jdbcType=CHAR},

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtSurvQuesDDao.updateCtSurvQuesD */
UPDATE CT_SURV_QUES_D

	 QUES_CONT = #{quesCont, jdbcType=NVARCHAR},
	 QUES_IMG_ATT_YN = #{quesImgAttYn, jdbcType=CHAR},
	 IMG_SURV_FILE_ID = #{imgSurvFileId, jdbcType=VARCHAR},
	 EXAM_CHOI_ITEM_CD = #{examChoiItemCd, jdbcType=VARCHAR},
	 MUL_CHOI_YN = #{mulChoiYn, jdbcType=CHAR},
	 MANDA_REPLY_YN = #{mandaReplyYn, jdbcType=CHAR},


     AND QUES_ID = #{quesId, jdbcType=VARCHAR}
     AND COMP_ID = #{compId, jdbcType=CHAR}
     AND SURV_ID = #{survId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.ct.dao.CtSurvQuesDDao.deleteCtSurvQuesD */
DELETE FROM CT_SURV_QUES_D

     AND QUES_ID = #{quesId, jdbcType=VARCHAR}
     AND COMP_ID = #{compId, jdbcType=CHAR}
     AND SURV_ID = #{survId, jdbcType=VARCHAR}
