DOC_GRP_ID, TASK_DT, USER_UID, TASK_CD, NOTE,
#{docGrpId, jdbcType=VARCHAR}, SYSDATE, NULL, TO_DATE(#{taskDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{userUid, jdbcType=VARCHAR}, #{taskCd, jdbcType=VARCHAR}, #{note, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.pt.dao.DmTaskHDao.updateDmTaskH */
