mapper.select:
/* com.innobiz.orange.web.or.dao.OrOrgHstRDao.selectOrOrgHstR */

SELECT

	M.*,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = M.HST_RESC_ID AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR},'ko')) HST_RESC_NM,

FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	T.ORG_ID,
	T.HST_ORG_ID,
	T.HST_RESC_ID,
	T.SORT_ORDR,
	
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = T.HST_RESC_ID AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR},'ko')) HST_RESC_NM,
	

FROM OR_ORG_HST_R T

	AND T.ORG_ID = #{orgId, jdbcType=VARCHAR}
	AND T.HST_ORG_ID = #{hstOrgId, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.ORG_ID, T.HST_ORG_ID

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.or.dao.OrOrgHstRDao.countOrOrgHstR */
SELECT COUNT(*) CNT
FROM OR_ORG_HST_R T

	AND T.ORG_ID = #{orgId, jdbcType=VARCHAR}
	AND T.HST_ORG_ID = #{hstOrgId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.or.dao.OrOrgHstRDao.insertOrOrgHstR */ INSERT INTO OR_ORG_HST_R
ORG_ID, HST_ORG_ID, HST_RESC_ID, SORT_ORDR,
#{orgId, jdbcType=VARCHAR}, #{hstOrgId, jdbcType=VARCHAR}, #{hstRescId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT NVL(MAX(SORT_ORDR),0)+1 FROM OR_ORG_HST_R WHERE ORG_ID = #{orgId, jdbcType=VARCHAR}),

mapper.update:
/* com.innobiz.orange.web.or.dao.OrOrgHstRDao.updateOrOrgHstR */
UPDATE OR_ORG_HST_R

	ORG_ID = #{orgId, jdbcType=VARCHAR},
	HST_ORG_ID = #{hstOrgId, jdbcType=VARCHAR},
	HST_RESC_ID = #{hstRescId, jdbcType=VARCHAR},
	SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},


	AND ORG_ID = #{orgId, jdbcType=VARCHAR}
	AND HST_ORG_ID = #{hstOrgId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.or.dao.OrOrgHstRDao.deleteOrOrgHstR */
DELETE FROM OR_ORG_HST_R

	AND ORG_ID = #{orgId, jdbcType=VARCHAR}
	AND HST_ORG_ID = #{hstOrgId, jdbcType=VARCHAR}
	${whereSqllet}
