mapper.select:
/* com.innobiz.orange.web.pt.dao.PtMnuDDao.countPtMnuD */
SELECT COUNT(*) CNT
FROM PT_MNU_D T

	AND T.MNU_ID = #{mnuId, jdbcType=VARCHAR}
	AND T.MNU_GRP_MD_CD = #{mnuGrpMdCd, jdbcType=VARCHAR}
	AND T.MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR}
	AND T.MNU_PID = #{mnuPid, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtMnuDDao.insertPtMnuD */ INSERT INTO PT_MNU_D
MNU_ID, MNU_GRP_MD_CD, MNU_GRP_ID, MNU_NM, RESC_ID, MNU_PID, SORT_ORDR, MNU_URL, MNU_TYP_CD, POP_SETUP_CONT, MNU_DESC, FLD_YN, SYS_MNU_YN, MD_RID, MD_ID, USE_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{mnuId, jdbcType=VARCHAR}, #{mnuGrpMdCd, jdbcType=VARCHAR}, #{mnuGrpId, jdbcType=VARCHAR}, #{mnuNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{mnuPid, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT NVL(MAX(SORT_ORDR),0)+1 FROM PT_MNU_D WHERE MNU_PID = #{mnuPid, jdbcType=VARCHAR}), #{mnuUrl, jdbcType=NVARCHAR}, #{mnuTypCd, jdbcType=VARCHAR}, #{popSetupCont, jdbcType=VARCHAR}, #{mnuDesc, jdbcType=NVARCHAR}, #{fldYn, jdbcType=VARCHAR}, #{sysMnuYn, jdbcType=VARCHAR}, #{mdRid, jdbcType=VARCHAR}, #{mdId, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, SYSDATE,SYSDATE,NULL, TO_DATE(#{regDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{modrUid, jdbcType=VARCHAR}, SYSDATE,NULL, TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtMnuDDao.updatePtMnuD */ UPDATE PT_MNU_D
MNU_ID = #{mnuId, jdbcType=VARCHAR}, MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR}, MNU_NM = #{mnuNm, jdbcType=NVARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, MNU_PID = #{mnuPid, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, MNU_URL = #{mnuUrl, jdbcType=NVARCHAR}, MNU_TYP_CD = #{mnuTypCd, jdbcType=VARCHAR}, POP_SETUP_CONT = #{popSetupCont, jdbcType=VARCHAR}, MNU_DESC = #{mnuDesc, jdbcType=NVARCHAR}, FLD_YN = #{fldYn, jdbcType=VARCHAR}, SYS_MNU_YN = #{sysMnuYn, jdbcType=VARCHAR}, MD_RID = #{mdRid, jdbcType=VARCHAR}, MD_ID = #{mdId, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, REGR_UID = #{regrUid, jdbcType=VARCHAR},
REG_DT = SYSDATE, REG_DT = NULL, REG_DT = TO_DATE(#{regDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),
MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = SYSDATE, MOD_DT = NULL, MOD_DT = TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),
AND MNU_ID = #{mnuId, jdbcType=VARCHAR} AND MNU_GRP_MD_CD = #{mnuGrpMdCd, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtMnuDDao.deletePtMnuD */
DELETE FROM PT_MNU_D

	AND MNU_ID = #{mnuId, jdbcType=VARCHAR}
	AND MNU_GRP_MD_CD = #{mnuGrpMdCd, jdbcType=VARCHAR}
	AND MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR}
	${whereSqllet}
