/* com.innobiz.orange.web.pt.dao.PtTermSetupDDao.selectPtTermSetupD */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	T.SETUP_CLS_ID,
	T.SETUP_ID,
	T.LANG_TYP_CD,
	T.TERM_VA,

FROM PT_TERM_SETUP_D T

	AND T.SETUP_CLS_ID = #{setupClsId, jdbcType=VARCHAR}
	AND T.SETUP_ID = #{setupId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.SETUP_CLS_ID, T.SETUP_ID, T.LANG_TYP_CD

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.pt.dao.PtTermSetupDDao.countPtTermSetupD */
SELECT COUNT(*) CNT
FROM PT_TERM_SETUP_D T

	AND T.SETUP_CLS_ID = #{setupClsId, jdbcType=VARCHAR}
	AND T.SETUP_ID = #{setupId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}

