/* com.innobiz.orange.web.wc.dao.WcApntrRDao.selectWcApntrR */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	WA_T.COMP_ID,WA_T.USER_UID,WA_T.APNTR_UID,

FROM WC_APNTR_R WA_T

ORDER BY ${orderBy}
ORDER BY WA_T.COMP_ID,WA_T.USER_UID,WA_T.APNTR_UID

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.wc.dao.WcApntrRDao.countWcApntrR */
