mapper.select:
/* com.innobiz.orange.web.wd.dao.WdAnbUseLDao.selectWdAnbUseL */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	T.YEAR,
	T.ANB_TYP_CD,
	T.ODUR_UID,
	TO_CHAR(T.USE_YMD, 'YYYY-MM-DD') USE_YMD,
	T.CMPL_YN,
	T.USE_CNT,
	TO_CHAR(T.REG_DT, 'YYYY-MM-DD HH24:MI:SS') REG_DT,
	T.APV_NO,
	T.RSON,

FROM WD_ANB_USE_L T

	AND T.YEAR = #{year, jdbcType=INTEGER}
	AND T.ANB_TYP_CD = #{anbTypCd, jdbcType=VARCHAR}
	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.USE_YMD = TO_DATE(#{useYmd, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS')
	AND T.CMPL_YN = #{cmplYn, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.YEAR, T.ANB_TYP_CD, T.ODUR_UID, T.USE_YMD

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.wd.dao.WdAnbUseLDao.countWdAnbUseL */
SELECT COUNT(*) CNT
FROM WD_ANB_USE_L T

	AND T.YEAR = #{year, jdbcType=INTEGER}
	AND T.ANB_TYP_CD = #{anbTypCd, jdbcType=VARCHAR}
	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.USE_YMD = TO_DATE(#{useYmd, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS')
	AND T.CMPL_YN = #{cmplYn, jdbcType=VARCHAR}
	${whereSqllet}

